package DailyProgramingPractice;

public class SwapTwoNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int a=20;
         int b=40;
         System.out.println(" first value before swping "+a);
         System.out.println(" second value before swaping"+b);
         int tem=a;
            a=b;
            b=tem;
            System.out.println("first value after swaping "+a);
            System.out.println("second value after swaping "+b);
	}

}
