package DailyProgramingPractice;

public class UppercaseAlphabet {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      char ch;
      for(  ch='A';ch<='Z';ch++) {
    	  System.out.print(ch + " ");
      }  
      System.out.println("******************************************");
      for(ch='a';ch<='z';++ch) {
    	  System.out.print(ch + " ");
      }
	}

}
