
public class StarPattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       for(int i =0;i<=5;i++) {
      for(int j=0;j<=4;j++) {
   	   System.out.println(" ");
    	   }
      for(int s=1;s<=i*2-1;s++) {
    	  System.out.println(" * ");
    	   }
    	  System.out.println();
       }
	}

}
