package day3;

public class UppercasedAlphabate {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
   char c;
   for(c='A';c<='Z';++c) {
	   System.out.print(c + " ");
   }
   System.out.println("***************************************");
   for(c='a';c<='z';c++) {
	   System.out.print(c + " ");
   }
	}

}
