package day4;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    for(int i=0;i<=3;++i) 
    	System.out.println("Hello"+i);
    for (int i=0;i<2;++i) 
    	System.out.println("\tHi"+i);
    for(int i=0;i<1;++i) 
    	System.out.println("\tok"+i);
    }
	}


