package day4;

public class NestedForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
   for( int i=0;i<=3;++i) {
	   System.out.println("hello "+i);
   }for(int j=0;j<=2;++j) {
	   System.out.println("\tbye "+j);
   }for(int k=0;k<=3;++k) {
	   System.out.println("\t\tok"+k);
   }
	}

}
